import React, { useState, useEffect, useRef, useCallback } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';
import { 
  AlertTriangle, 
  CheckCircle, 
  X, 
  Minimize2, 
  Maximize2, 
  GripHorizontal,
  Volume2,
  VolumeX,
  Eye,
  EyeOff
} from 'lucide-react';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const PopupOverlay = ({ 
  isEnabled, 
  onClose, 
  initialOpacity = 0.9,
  initialPosition = { x: 20, y: 20 }
}) => {
  const webcamRef = useRef(null);
  const overlayRef = useRef(null);
  const [position, setPosition] = useState(initialPosition);
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [opacity, setOpacity] = useState(initialOpacity);
  const [isMinimized, setIsMinimized] = useState(false);
  const [currentStatus, setCurrentStatus] = useState('unknown');
  const [lastBadPostureTime, setLastBadPostureTime] = useState(null);
  const [soundEnabled, setSoundEnabled] = useState(true);
  const [showCamera, setShowCamera] = useState(true);
  const [isTabVisible, setIsTabVisible] = useState(true);
  const [badPostureStreak, setBadPostureStreak] = useState(0);

  // Audio notification
  const playNotificationSound = useCallback(() => {
    if (!soundEnabled) return;

    const audioContext = new (window.AudioContext || window.webkitAudioContext)();
    const oscillator = audioContext.createOscillator();
    const gainNode = audioContext.createGain();

    oscillator.connect(gainNode);
    gainNode.connect(audioContext.destination);

    oscillator.frequency.value = 440;
    oscillator.type = 'sine';
    gainNode.gain.setValueAtTime(0.3, audioContext.currentTime);
    gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.5);

    oscillator.start(audioContext.currentTime);
    oscillator.stop(audioContext.currentTime + 0.5);
  }, [soundEnabled]);

  // Tab visibility detection
  useEffect(() => {
    const handleVisibilityChange = () => {
      setIsTabVisible(!document.hidden);
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => document.removeEventListener('visibilitychange', handleVisibilityChange);
  }, []);

  // Posture detection loop
  useEffect(() => {
    if (!isEnabled) return;

    const detectPosture = async () => {
      if (!webcamRef.current) return;

      const imageSrc = webcamRef.current.getScreenshot();
      if (!imageSrc) return;

      try {
        const response = await axios.post(`${API_URL}/detect`, {
          image: imageSrc
        }, { timeout: 5000 });

        if (response.data.success) {
          const status = response.data.overall_status;
          setCurrentStatus(status);

          if (status === 'bad') {
            setBadPostureStreak(prev => prev + 1);

            // Notify after 3 consecutive bad posture detections
            if (badPostureStreak >= 2) {
              setLastBadPostureTime(new Date());
              playNotificationSound();

              // Show browser notification if tab is not visible
              if (!isTabVisible && Notification.permission === 'granted') {
                new Notification('PostureGuard Alert', {
                  body: 'Please correct your sitting posture!',
                  icon: '/favicon.ico',
                  tag: 'posture-alert'
                });
              }
            }
          } else {
            setBadPostureStreak(0);
          }
        }
      } catch (err) {
        console.error('Popup detection error:', err);
      }
    };

    const interval = setInterval(detectPosture, 1000);
    return () => clearInterval(interval);
  }, [isEnabled, isTabVisible, playNotificationSound, badPostureStreak]);

  // Request notification permission
  useEffect(() => {
    if (isEnabled && Notification.permission === 'default') {
      Notification.requestPermission();
    }
  }, [isEnabled]);

  // Drag handlers
  const handleMouseDown = (e) => {
    if (e.target.closest('.popup-drag-handle')) {
      setIsDragging(true);
      const rect = overlayRef.current.getBoundingClientRect();
      setDragOffset({
        x: e.clientX - rect.left,
        y: e.clientY - rect.top
      });
    }
  };

  const handleMouseMove = useCallback((e) => {
    if (!isDragging) return;

    const newX = Math.max(0, Math.min(window.innerWidth - 200, e.clientX - dragOffset.x));
    const newY = Math.max(0, Math.min(window.innerHeight - 100, e.clientY - dragOffset.y));

    setPosition({ x: newX, y: newY });
  }, [isDragging, dragOffset]);

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  useEffect(() => {
    if (isDragging) {
      window.addEventListener('mousemove', handleMouseMove);
      window.addEventListener('mouseup', handleMouseUp);
    }
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging, handleMouseMove]);

  if (!isEnabled) return null;

  const getStatusConfig = () => {
    switch (currentStatus) {
      case 'good':
        return {
          bgColor: 'bg-posture-good/20',
          borderColor: 'border-posture-good/50',
          textColor: 'text-posture-good',
          icon: <CheckCircle className="w-5 h-5" />,
          label: 'Good Posture',
          glowColor: 'shadow-posture-good/30'
        };
      case 'bad':
        return {
          bgColor: 'bg-posture-bad/20',
          borderColor: 'border-posture-bad/50',
          textColor: 'text-posture-bad',
          icon: <AlertTriangle className="w-5 h-5" />,
          label: 'Fix Your Posture!',
          glowColor: 'shadow-posture-bad/30'
        };
      default:
        return {
          bgColor: 'bg-white/10',
          borderColor: 'border-white/20',
          textColor: 'text-white/70',
          icon: null,
          label: 'Detecting...',
          glowColor: ''
        };
    }
  };

  const statusConfig = getStatusConfig();

  return (
    <div
      ref={overlayRef}
      className={`popup-overlay ${isDragging ? 'dragging' : ''}`}
      style={{
        left: position.x,
        top: position.y,
        opacity: opacity
      }}
      onMouseDown={handleMouseDown}
    >
      <div 
        className={`
          ${isMinimized ? 'w-auto' : 'w-72'}
          rounded-2xl overflow-hidden
          backdrop-blur-xl
          ${statusConfig.bgColor}
          border ${statusConfig.borderColor}
          shadow-2xl ${statusConfig.glowColor}
          transition-all duration-300
        `}
      >
        {/* Header / Drag Handle */}
        <div className="popup-drag-handle flex items-center justify-between px-3 py-2 bg-black/30 border-b border-white/10">
          <div className="flex items-center gap-2">
            <GripHorizontal className="w-4 h-4 text-white/40" />
            {isMinimized && (
              <div className={`flex items-center gap-2 ${statusConfig.textColor}`}>
                {statusConfig.icon}
                <span className="text-sm font-medium">{statusConfig.label}</span>
              </div>
            )}
          </div>

          <div className="flex items-center gap-1">
            <button
              onClick={() => setShowCamera(!showCamera)}
              className="p-1.5 rounded-lg hover:bg-white/10 transition-colors"
              title={showCamera ? 'Hide camera' : 'Show camera'}
            >
              {showCamera ? (
                <Eye className="w-4 h-4 text-white/60" />
              ) : (
                <EyeOff className="w-4 h-4 text-white/60" />
              )}
            </button>
            <button
              onClick={() => setSoundEnabled(!soundEnabled)}
              className="p-1.5 rounded-lg hover:bg-white/10 transition-colors"
              title={soundEnabled ? 'Mute' : 'Unmute'}
            >
              {soundEnabled ? (
                <Volume2 className="w-4 h-4 text-white/60" />
              ) : (
                <VolumeX className="w-4 h-4 text-white/60" />
              )}
            </button>
            <button
              onClick={() => setIsMinimized(!isMinimized)}
              className="p-1.5 rounded-lg hover:bg-white/10 transition-colors"
            >
              {isMinimized ? (
                <Maximize2 className="w-4 h-4 text-white/60" />
              ) : (
                <Minimize2 className="w-4 h-4 text-white/60" />
              )}
            </button>
            <button
              onClick={onClose}
              className="p-1.5 rounded-lg hover:bg-red-500/20 transition-colors"
            >
              <X className="w-4 h-4 text-white/60" />
            </button>
          </div>
        </div>

        {!isMinimized && (
          <>
            {/* Camera Preview */}
            {showCamera && (
              <div className="relative aspect-video bg-black/50">
                <Webcam
                  ref={webcamRef}
                  audio={false}
                  screenshotFormat="image/jpeg"
                  videoConstraints={{
                    width: 320,
                    height: 180,
                    facingMode: "user"
                  }}
                  className="w-full h-full object-cover"
                />

                {/* Status Badge Overlay */}
                <div className={`absolute bottom-2 left-2 right-2 px-3 py-1.5 rounded-lg ${statusConfig.bgColor} border ${statusConfig.borderColor} backdrop-blur-sm`}>
                  <div className={`flex items-center justify-center gap-2 ${statusConfig.textColor}`}>
                    {statusConfig.icon}
                    <span className="text-sm font-semibold">{statusConfig.label}</span>
                  </div>
                </div>
              </div>
            )}

            {/* Hidden webcam for detection when camera preview is off */}
            {!showCamera && (
              <Webcam
                ref={webcamRef}
                audio={false}
                screenshotFormat="image/jpeg"
                videoConstraints={{
                  width: 320,
                  height: 180,
                  facingMode: "user"
                }}
                className="hidden"
              />
            )}

            {/* Opacity Slider */}
            <div className="px-4 py-3">
              <div className="flex items-center justify-between mb-2">
                <span className="text-xs text-white/50">Opacity</span>
                <span className="text-xs text-white/50">{Math.round(opacity * 100)}%</span>
              </div>
              <input
                type="range"
                min="0.2"
                max="1"
                step="0.05"
                value={opacity}
                onChange={(e) => setOpacity(parseFloat(e.target.value))}
                className="w-full h-1.5 bg-white/20 rounded-lg appearance-none cursor-pointer accent-emerald-500"
              />
            </div>

            {/* Last Alert Time */}
            {lastBadPostureTime && (
              <div className="px-4 pb-3">
                <p className="text-xs text-white/40 text-center">
                  Last alert: {lastBadPostureTime.toLocaleTimeString()}
                </p>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default PopupOverlay;
